import { Component, OnDestroy, OnInit } from '@angular/core';

import { MockDataService } from '../../shared/services/mock-data.service';

import { CalculationLoanService } from '../../shared/services/calculation-loan.service';
import { filter, map, Observable, of, repeat, Subject, switchMap, take, takeUntil } from 'rxjs';

@Component({
  selector: 'tes-documents-payments-page',
  templateUrl: './documents-payments-page.component.html',
  styleUrls: ['./documents-payments-page.component.scss'],
})
export class DocumentsPaymentsPageComponent implements OnInit, OnDestroy {
  public currentDocuments: {
    osago: any[],
    casco: any[],
  } = {
    osago: [
      {
        type: 'ОСАГО',
        document_list: [],
      },
    ], casco: [],
  };
  public cascoStatus!: string;

  public cascoIsApplied!: boolean;

  private checkCascoStatus$!: Observable<any>;
  private getCascoDocuments$!: Observable<any>;

  private _unsubscribeAll: Subject<void> = new Subject();

  constructor(
    private _mockDataService: MockDataService,
    private calculateLoanService: CalculationLoanService,
  ) {
  }

  ngOnInit(): void {
    this.getCascoDocuments();
    this.getOsagoDocuments();
  }

  getCascoDocuments(): void {
    if (localStorage.getItem('createdCascoOffers')) {
      this.currentDocuments.casco =
        [JSON.parse(localStorage.getItem('createdCascoOffers') as string)];
    }
    console.log('current documents: ', this.currentDocuments);
    this.currentDocuments.casco[0] = {...this.currentDocuments.casco[0], type: 'КАСКО' };

    this.attachFilesAndStartBooking();

    this.checkCascoStatus$ = this.calculateLoanService.checkCascoStatus(
      localStorage.getItem('casco_calculation_id') as string,
    ).pipe(
      repeat({ delay: 60000 }),
      filter((data: any) => {
          if (data.message.status === 'Не действует (Согласован)') {
            this.cascoIsApplied = true;
            return true;
          } else if (data.message.status === 'Аннулирован (Аннулирован)') {
            this.cascoIsApplied = false;
            return true;
          }

          this.cascoIsApplied = false;
          return false;
        },
      ),
      take(1),
      takeUntil(this._unsubscribeAll),
    );

    this.getCascoDocuments$ = this.calculateLoanService
      .getCascoDocuments(localStorage.getItem('casco_calculation_id') as string)
      .pipe(
        map(data => {
          if (this.currentDocuments.casco[0].document_list.length === 2) {
            data.links.forEach((item: any) => {
              this.currentDocuments.casco[0].document_list.push(
                {
                  documentTypeName: item.name,
                  url: item.link,
                },
              );
            });

            this.currentDocuments.casco[0].document_list[2].documentTypeName = data.links[0].name;
            this.currentDocuments.casco[0].document_list[2].url = data.links[0].link;
            this.currentDocuments.casco[0].document_list[3].documentTypeName = data.links[1].name;
            this.currentDocuments.casco[0].document_list[3].url = data.links[1].link;
          }
        }),
        takeUntil(this._unsubscribeAll),
      );

    this.issueCascoPolicy(); // remove when we will start to get right status from the checkCascoStatus query
  }

  attachFilesAndStartBooking(): void {
    this.currentDocuments.casco[0].document_list.forEach((item: any, i: number) => {
      this.currentDocuments.casco[0].document_list[i] = {
        ...this.currentDocuments.casco[0].document_list[i],
        file_type: 'image/png',
        file: '',
      };
      delete this.currentDocuments.casco[0].document_list[i].doc_status;
      delete this.currentDocuments.casco[0].document_list[i].is_doc_send;
      delete this.currentDocuments.casco[0].document_list[i].objectDescription;
    });

    const body = this.currentDocuments.casco[0].document_list;

    this
      .calculateLoanService
      .attachFilesToCasco(body, localStorage.getItem('casco_calculation_id') as string)
      .pipe(
        map(data => {
            return data.status === 'Файлы загружены';
          },
        ),
        switchMap(() =>
          this.calculateLoanService
            .startCascoBooking(
              {},
              localStorage.getItem('casco_calculation_id') as string)),
        switchMap(() => of(this.checkCascoStatus$.subscribe(result => console.log(result)))),
        takeUntil(this._unsubscribeAll),
      )
      .subscribe(r => this.checkCascoStatus$.subscribe(result => console.log(result)));
  }

  issueCascoPolicy(): void {
    this.calculateLoanService
      .issueCascoPolicy(localStorage.getItem('casco_calculation_id') as string)
      .pipe(
        map(data => {
          switch (data.status) {
            case 200:
              this.cascoStatus = 'Не действует (Расчет)';
              break;
            case 201:
              this.cascoStatus = 'Не действует (Согласован)';
              break;
            case 400:
              this.cascoStatus = 'Аннулирован (Отказ)';
              break;
          }
        }),
        switchMap(() => of(this.getCascoDocuments$.subscribe())),
        takeUntil(this._unsubscribeAll),
      )
      .subscribe();
  }

  getOsagoDocuments(): void {
    this.calculateLoanService.getOsagoAnketa('')
      .pipe(
        map(data => {
          if (data.status === 'success')
            this.currentDocuments.osago[0].document_list.push(
              {
                documentTypeName: 'ОСАГО Заявление',
                url: 'data:application/pdf;base64,' + data.result.policyPdf,
              },
            );
        }),
        takeUntil(this._unsubscribeAll),
      ).subscribe();
  }

  getPayForPolicyLink(): void {
    this.calculateLoanService.getPayForPolicyLink('')
      .pipe(
        map((data) => {
          window.open(data.payment_url, '_blank');
        }),
      )
      .subscribe();
  }

  getPdfPolicy(): void {
    this.calculateLoanService.getPdfPolicy({}, '')
      .pipe(
        map((data) => {
          if (data.message.status === 400) console.error(data.message.status);
          if (data.message.status === 200)
            this.currentDocuments.osago[0].document_list.push(
              {
                documentTypeName: 'Полис ОСАГО',
                url: data.body.policy_pdf_url,
              },
            );
        }),
      )
      .subscribe();
  }

  ngOnDestroy(): void {
    this._unsubscribeAll.next();
    this._unsubscribeAll.complete();
  }
}
